// Taken from Banjo Kazooie decomp
void glcrc_calc_checksum(void *start, void *end)
{
    u8 *p;

    u32 shift = 0;

    u64 seed = 0x8F809F473108B3C1;

    u32 crc1 = 0;
    u32 crc2 = 0;

    u32 tmp;

    typedef u32 func(u64 *seed);
    func* func_8025C29C = (func*)funct;

    // CRC1: Iterate forwards over bytes
    for (p = start; (void *)p < end; p++)
    {
        seed += *p << (shift & 15);

        tmp = func_8025C29C(&seed);

        shift += 7;

        crc1 ^= tmp;
    }

    // CRC2: Iterate backwards over bytes
    for (p = (u8 *)end - 1; (void *)p >= start; p--)
    {
        seed += *p << (shift & 15);

        tmp = func_8025C29C(&seed);

        shift += 3;

        crc2 ^= tmp;
    }

    sns_data[30] = crc1;
    sns_data[31] = crc2;
}



void NpcM_StopNSwop(NpcMaker* npc, PlayState* play)
{
    int j = 0;

    for (int i = 0; i < 7; i++)
    {
        if (npc->scriptVars[i])
        {
          sns_data[1 + j] = 0x108 + i;
          j++;
        }
    }

    glcrc_calc_checksum(&sns_data, &sns_data[30]);
}
